"use strict";var ajaxResposta=document.querySelector("#ajax-response-cep");function buscarCep(){if(!valida_input_cep(cep.value))return!1;let e=`https://viacep.com.br/ws/${cep.value}/json/`;fetch(e).then(e=>e.json()).then(e=>{preenche_formulario(e)}).catch(e=>{console.error("Failed retrieving information",e)})}function valida_input_cep(e){if(""==cep.value)return ajaxResposta.textContent="Informe o cep valido!",!1;let t=e.replace(/\D/g,"");return!!/^[0-9]{8}$/.test(t)||(ajaxResposta.textContent="CEP deve ter 8 digitos!",cep.focus())}function limpa_forumlario(){document.querySelector("#cep").value="",document.querySelector("#endereco").value="",document.querySelector("#numero").value="",document.querySelector("#complemento").value="",document.querySelector("#bairro").value="",document.querySelector("#cidade").value="",document.querySelector("#uf").value=""}function preenche_formulario(e){if(e.erro)return ajaxResposta.textContent="Preencha um CEP v√°lido!",!1;ajaxResposta.textContent="",document.querySelector("#logradouro").value=e.logradouro,document.querySelector("#bairro").value=e.bairro,document.querySelector("#cidade").value=e.localidade,document.querySelector("#uf").value=e.uf}cep.addEventListener("blur",buscarCep,!1);