"use strict";const cep=document.querySelector("#cep"),spanResponse=document.querySelector("#response");function buscarCep(){if(!valida_input_cep(cep.value))return!1;let e=`https://viacep.com.br/ws/${cep.value}/json/`;fetch(e).then(e=>e.json()).then(e=>{preenche_formulario(e)}).catch(e=>{console.error("Failed retrieving information",e)})}function valida_input_cep(e){if(""==cep.value)return spanResponse.textContent="Informe o cep valido!",!1;let o=e.replace(/\D/g,"");return!!/^[0-9]{8}$/.test(o)||(spanResponse.textContent="CEP deve ter 8 digitos!",cep.focus())}function limpa_forumlario(){document.querySelector("#cep").value="",document.querySelector("#endereco").value="",document.querySelector("#numero").value="",document.querySelector("#complemento").value="",document.querySelector("#bairro").value="",document.querySelector("#cidade").value="",document.querySelector("#uf").value=""}function preenche_formulario(e){if(e.erro)return spanResponse.textContent="Preencha um CEP val√≠do!",!1;document.querySelector("#logradouro").value=e.logradouro,document.querySelector("#bairro").value=e.bairro,document.querySelector("#cidade").value=e.localidade,document.querySelector("#uf").value=e.uf}cep.addEventListener("blur",buscarCep,!1);